mapper.select:
select id, course_id, name, start_time, end_time, status, semester_id from course
where 1 = 1

    and course_id = #{courseId}


    and name = #{name}


    and start_time = #{startTime}


    and end_time = #{endTime}


    and status = #{status}


    and semester_id = #{semesterId}

select id, course_id, name, start_time, end_time, status, semester_id from course
where
start_time > now()

    and course_id = #{courseId}


    and name = #{name}


    and status = #{status}


    and semester_id = #{semesterId}

select id, course_id, name, start_time, end_time, status, semester_id from course
where
    now() > start_time and end_time > now()

    and course_id = #{courseId}


    and name = #{name}


    and status = #{status}


    and semester_id = #{semesterId}

select id, course_id, name, start_time, end_time, status, semester_id from course
where
    now() > end_time

    and course_id = #{courseId}


    and name = #{name}


    and status = #{status}


    and semester_id = #{semesterId}
