mapper.insert:
insert into student (student_id, name, sex, phone_number, email, administrative_class) values
(#{student.studentId}, #{student.name}, #{student.sex}, #{student.phoneNumber}, #{student.email}, #{student.administrativeClass})
on duplicate key update sex = values (sex), phone_number = values (phone_number), email = values (email), administrative_class = values (administrative_class);

mapper.select:
select id, student_id, name, sex, phone_number, email, administrative_class from student
where student_id in

        #{studentId}

select id, student_id, name, sex, phone_number, email, administrative_class from student
where 1 = 1

    and name = #{name}


    and studen_id = #{studentId}


    and sex = #{sex}


    and email = #{email}


    and phone_number = #{phoneNumber}


    and administrative_class = #{administrativeClass}
